,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'v_chk number;',
'v_num NUMBER;',
'',
'begin',
'update customer_info',
'set ',
'                     vc_customer_name     = :P19_CUSTOMER_NAME ,',
'                     nu_mobile_num        = :P19_MOBILE_NUM,',
'                     vc_email_id          = :P19_EMAIL_ID,',
'                     vc_remarks           = :P19_REMARKS,',
'                     VC_CUSTOMER_ADDRESS  =  :P19_VC_CUSTOMER_ADDRESS,',
'                     VC_CUSTOMER_CATEGORY =  :P19_VC_CUSTOMER_CATEGORY,',
'                     VC_CITY              =  :P19_CITY,',
'                     VC_AREA               =  :P19_AREA,',
'                     VC_COUNTRY            = :P19_COUNTRY,',
'                     VC_CONTACT_PERSON  =    :P19_CONTACT_PERSON,',
'                     VC_CP_PHONE_NUMBER   = :P19_CP_PHONE_NUMBER,',
'                     DT_MOD_DATE         = SYSDATE,',
'                     VC_AUTH_CODE       =  :GLOBAL_USER_CODE,',
'                     VC_CUST_TYPE     = :P19_CUSTOMER_TYPE',
'WHERE VC_COMP_CODE= :GLOBAL_COMP_CODE',
'AND  NU_CUST_ID  = :P19_CUSTOMER_ID;',
'',
'begin',
'',
'select count(*) into v_chk from mst_cust_location',
'WHERE VC_COMP_CODE= :GLOBAL_COMP_CODE',
'AND  NU_CUSTOMER_CODE  = :P19_CUSTOMER_ID',
'AND   vc_cust_type  = ''S'' ;',
'',
'if v_chk = 0 then',
'',
' insert into mst_cust_location',
'           (',
'           VC_COMP_CODE,',
'           VC_DEFAULT_COMP,',
'           NU_CUSTOMER_CODE,',
'           VC_AUTH_CODE,',
'           DT_MOD_DATE ,',
'           NU_LATITUDE,',
'           NU_LONGITUDE,',
'           VC_PHY_ADDRESS,',
'               vc_cust_type',
'           )',
'           values',
'           (',
'           :GLOBAL_COMP_CODE,',
'           :GLOBAL_PARENT_COMP,',
'           :P19_CUSTOMER_ID,',
'           :GLOBAL_USER_CODE,',
'            SYSDATE,',
'            :P19_LATITUDE,',
'            :P19_LONGITUDE,',
'            :P19_VC_LOCATION,',
'             ''S''',
'           ',
'           );',
'else',
'',
'update mst_cust_location ',
'set        NU_LATITUDE  = :P19_LATITUDE,',
'           NU_LONGITUDE =  :P19_LONGITUDE,',
'           VC_PHY_ADDRESS=  :P19_VC_LOCATION',
'WHERE  VC_COMP_CODE = :GLOBAL_COMP_CODE',
'AND   NU_CUSTOMER_CODE = :P19_CUSTOMER_ID',
'AND   vc_cust_type  = ''S'' ;',
'',
'END IF;',
