,p_plug_name=>'Customer Statement Report'
,p_region_template_options=>'#DEFAULT#:t-Region--accent1:t-Region--scrollBody'
,p_plug_template=>wwv_flow_imp.id(38660781796867965)
,p_plug_display_sequence=>20
,p_include_in_reg_disp_sel_yn=>'Y'
,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select  vc_account_name,VC_GROUP_NAME, vc_curr_description,Vc_Voucher_no,',
' Dt_Voucher_Date, Dr_Amount,Cr_Amount,ABS(Running_Bal)BAL_RUN,VC_Remarks,Decode(case when running_bal>=0 then 1 when running_bal<0 then 2 end,1,''Dr'',2,''Cr'') Run_TYPE',
',vc_vou_no,cheque_no',
'from(',
'SELECT vc_vou_no,',
' vc_account_name,VC_GROUP_NAME, vc_curr_description,Vc_Voucher_no,',
' Dt_Voucher_Date, Dr_Amount,Cr_Amount,',
'DECODE (VC_Type,1, Run_Bal ,',
'                    SUM (Dr_Amount)',
'                         OVER (PARTITION BY nu_account_code, VC_COMP_CODE --,VC_TYPE',
'                               ORDER BY',
'                                   nu_account_code,',
'                                   Dt_Voucher_Date,',
'                                   Vc_Voucher_no,',
'                                   VC_COMP_CODE)',
'                  - SUM (Cr_Amount)',
'                         OVER (PARTITION BY nu_account_code, VC_COMP_CODE --,VC_TYPE,',
'                               ORDER BY',
'                                   nu_account_code,',
'                                   Dt_Voucher_Date,',
'                                   Vc_Voucher_no,',
'                                   VC_COMP_CODE)',
'                   - Run_Bal',
'                   )',
'Running_Bal,VC_Remarks,cheque_no',
'FROM (SELECT a.vc_comp_code,D.DT_FIN_START_DATE,D.DT_FIN_END_DATE,a.nu_account_code,a.vc_account_name,',
'             b.vc_group_code,b.VC_GROUP_NAME,d.nu_currency_code,vc_notation,',
'             vc_curr_description,''Base Currency''     VC_Curr,''OP BAL''            Vc_Voucher_no,',
'             D.DT_FIN_START_DATE Dt_Voucher_Date,1                   VC_Type,',
'             DECODE (d.VC_YEAR_OPEN_BALANCE_TYPE,',
'                           ''Dr'', d.NU_YEAR_OPEN_BALANCE,',
'                           0)',
'                       Dr_Amount,',
'             DECODE (d.VC_YEAR_OPEN_BALANCE_TYPE,',
'                           ''Cr'', d.NU_YEAR_OPEN_BALANCE,',
'                           0)',
'                       Cr_Amount,',
'                   ''Opening Balance ''  VC_Remarks,',
'             DECODE (d.VC_YEAR_OPEN_BALANCE_TYPE,',
'                           ''Dr'', d.NU_YEAR_OPEN_BALANCE,',
'                           -d.NU_YEAR_OPEN_BALANCE)',
'                       Run_Bal,',
'                   ''OPEN''              VC_VOU_NO,',
'                   ''Y''                 auth_flag,',
'                   ''''cheque_no',
'              FROM Finance.Mst_Account          a,',
'                   Finance.Mst_Group            b,',
'                   Finance.mst_currency         c,',
'                   Finance.Mst_Account_Balance  d',
'             WHERE     a.vc_comp_code = b.vc_comp_code',
'                   AND a.vc_acc_group_code = b.vc_group_code',
'                   AND a.vc_comp_code = d.vc_comp_code',
'                   AND a.nu_account_code = d.nu_account_code',
'                   AND a.vc_comp_code = c.vc_comp_code',
'                --    AND a.NU_CURRENCY_CODE = c.nu_currency_code',
'            UNION',
'              SELECT a.vc_comp_code,',
'                     E.DT_CURRENT_START_DATE,',
'                     E.DT_CURRENT_END_DATE,',
'                     a.nu_account_code,',
'                     a.vc_account_name,',
'                     b.vc_group_code,',
'                     b.VC_GROUP_NAME,',
'                     d.nu_currency_code,',
'                     vc_notation,',
'                     vc_curr_description,',
'                     ''Base Currency''         VC_Curr,',
'                     SUBSTR (d.Vc_Voucher_no, 3),',
'                     D.Dt_Voucher_Date,',
'                     2                       VC_Type,',
'                     SUM (DECODE (d.CH_BALANCE_TYPE, ''Dr'', d.NU_AMOUNT, 0))',
'                         Dr_Amount,',
'                     SUM (DECODE (d.CH_BALANCE_TYPE, ''Cr'', d.NU_AMOUNT, 0))',
'                         Cr_Amount,',
'                     d.vc_narration          VC_Remarks,',
'                     0                       Run_Bal,',
'                     d.VC_VOUCHER_NO           VC_VOU_NO,',
'                     NVL (d.ch_auth_Flag, ''N'') auth_flag,',
'                     VC_CHEQUE_NO cheque_no',
'                FROM Finance.Mst_Account     a,',
'                     Finance.Mst_Group       b,',
'                     Finance.mst_currency    c,',
'                     Finance.DT_Transaction  d,',
'                     Finance.Fin_Account_Year e,',
'                     Finance.DT_CHEQUE_REGISTER r ',
'               WHERE     a.vc_comp_code = b.vc_comp_code',
'                     AND a.vc_acc_group_code = b.vc_group_code',
'                     AND a.vc_comp_code = d.vc_comp_code',
'                     AND a.nu_account_code = d.nu_account_code',
'                     AND d.vc_comp_code = e.vc_comp_code',
'                     AND a.vc_comp_code = c.vc_comp_code',
'                    --  AND a.NU_CURRENCY_CODE = c.nu_currency_code',
'                     AND NVL (d.ch_auth_flag, ''N'') = ''Y''',
'                     and d.vc_comp_code = r.vc_comp_code(+)',
'                     and d.VC_VOUCHER_NO  = r.VC_VOUCHER_NO(+)',
'                     and  d.DT_VOUCHER_DATE = r.DT_VOUCHER_DATE (+)',
'                     AND d.dt_voucher_date BETWEEN E.DT_CURRENT_START_DATE',
'                                               AND E.DT_CURRENT_END_DATE',
'            GROUP BY a.vc_comp_code,',
'                     E.DT_CURRENT_START_DATE,',
'                     E.DT_CURRENT_END_DATE,',
'                     a.nu_account_code,',
'                     a.vc_account_name,',
'                     b.vc_group_code,',
'                     b.VC_GROUP_NAME,',
'                     d.nu_currency_code,',
'                     vc_notation,',
'                     vc_curr_description,',
'                     ''Base Currency'',',
'                     SUBSTR (d.Vc_Voucher_no, 3),',
'                     D.Dt_Voucher_Date,',
'                     2,',
'                     d.vc_narration,',
'                     0,',
'                     d.VC_VOUCHER_NO,',
'                     NVL (d.ch_auth_Flag, ''N''),',
'                    r.VC_CHEQUE_NO',
'                     ',
'                     )',
'where vc_Comp_code = :GLOBAL_COMP_CODE',
'and dt_voucher_date between TO_DATE(:P26_FROM_DATE,''DD-MM-YYYY'') and TO_DATE(:P26_TO_DATE,''DD-MM-YYYY'')',
'and TO_CHAR(TRIM(NU_ACCOUNT_CODE)) = TO_CHAR(TO_NUMBER(TRIM(:P26_CUSTOMER))) ',
'and dt_fin_start_date >= TO_DATE(:P26_START_YEAR,''DD-MM-YYYY'')',
');'))
